"use strict";

var path = require("path");

var chalk = require("chalk");

var generateBuilders = require("./generators/generateBuilders");

var generateValidators = require("./generators/generateValidators");

var generateAsserts = require("./generators/generateAsserts");

var generateConstants = require("./generators/generateConstants");

var format = require("../../../scripts/utils/formatCode");

var writeFile = require("../../../scripts/utils/writeFileAndMkDir");

var baseDir = path.join(__dirname, "../src");
console.log("Generating @babel/types dynamic functions");
var buildersFile = path.join(baseDir, "builders/generated/index.js");
writeFile(buildersFile, format(generateBuilders(), buildersFile));
console.log("  ".concat(chalk.green("✔"), " Generated builders"));
var validatorsFile = path.join(baseDir, "validators/generated/index.js");
writeFile(validatorsFile, format(generateValidators(), validatorsFile));
console.log("  ".concat(chalk.green("✔"), " Generated validators"));
var assertsFile = path.join(baseDir, "asserts/generated/index.js");
writeFile(assertsFile, format(generateAsserts(), assertsFile));
console.log("  ".concat(chalk.green("✔"), " Generated asserts"));
var constantsFile = path.join(baseDir, "constants/generated/index.js");
writeFile(constantsFile, format(generateConstants(), constantsFile));
console.log("  ".concat(chalk.green("✔"), " Generated constants"));