#!/usr/bin/env node
'use strict';

var fs = require('fs');

var path = require('path');

var check = require('check-types');

var bfj = require('../src');

console.log('reading json');
var time = process.hrtime();
bfj.read(getDataPath('.json')).then(function (data) {
  reportTime();
  console.log('writing json');
  return bfj.write(getDataPath('-result.json'), data);
}).then(function () {
  return done('succeeded');
})["catch"](function (error) {
  return done(error.stack, 1);
});

function getDataPath(suffix) {
  return path.resolve(__dirname, process.argv[2] + suffix);
}

function reportTime() {
  var interimTime = process.hrtime(time);
  console.log('%d seconds and %d nanoseconds', interimTime[0], interimTime[1]);
  time = process.hrtime();
}

function done(message, code) {
  reportTime();
  console.log(message);
  process.exit(code);
}