'use strict';

var fs = require('fs');

module.exports = function (fp) {
  return new Promise(function (resolve) {
    fs.access(fp, function (err) {
      resolve(!err);
    });
  });
};

module.exports.sync = function (fp) {
  try {
    fs.accessSync(fp);
    return true;
  } catch (err) {
    return false;
  }
};