"use strict";

var qs = require('querystring'); // these are built-in query parameters so should be ignored
// if the user happen to add them as attrs


var ignoreList = ['id', 'index', 'src', 'type']; // transform the attrs on a SFC block descriptor into a resourceQuery string

exports.attrsToQuery = function (attrs, langFallback) {
  var query = "";

  for (var name in attrs) {
    var value = attrs[name];

    if (!ignoreList.includes(name)) {
      query += "&".concat(qs.escape(name), "=").concat(value ? qs.escape(value) : "");
    }
  }

  if (langFallback && !("lang" in attrs)) {
    query += "&lang=".concat(langFallback);
  }

  return query;
};