/* eslint-disable
  strict,
  no-param-reassign
*/
'use strict';

var fs = require('fs');

var path = require('path');

var Ajv = require('ajv');

var errors = require('ajv-errors');

var keywords = require('ajv-keywords');

var ValidationError = require('./ValidationError');

var ajv = new Ajv({
  allErrors: true,
  jsonPointers: true
});
errors(ajv);
keywords(ajv, ['instanceof', 'typeof']);

var validateOptions = function validateOptions(schema, options, name) {
  if (typeof schema === 'string') {
    schema = fs.readFileSync(path.resolve(schema), 'utf8');
    schema = JSON.parse(schema);
  }

  if (!ajv.validate(schema, options)) {
    throw new ValidationError(ajv.errors, name);
  }

  return true;
};

module.exports = validateOptions;